import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import './ShowVecinos.css'

const endpoint = 'http://localhost:8000/api'

const ShowVecinos = () => {
    const [vecinos, setVecinos] = useState([])

    useEffect(() => {
        getAllVecinos()
    }, [])

    const getAllVecinos = async () => {
        const response = await axios.get(`${endpoint}/vecinos`)
        setVecinos(response.data);

    }
    const deleteVecino = async (id) => {
        await axios.delete(`${endpoint}/vecino/${id}`)
        getAllVecinos()
    }

    return (
        <div>
       
            <h1>VECINOS DE LA COMUNIDAD</h1>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>Piso</th>
                    </tr>
                </thead>
                <tbody>

                    {vecinos.map((vecino) => (
                        <tr key={vecino.id}>
                            <td>{vecino.id}</td>
                            <td>{vecino.nombre}</td>
                            <td>{vecino.apellido}</td>
                            <td>{vecino.piso}</td>
                            <td>
                                <Link to={`/edit/${vecino.id}`} className='btn btn-primary mr-2'> EDIT </Link>
                                <button onClick={() => deleteVecino(vecino.id)} className='btn btn-danger'>eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

export default ShowVecinos
