import React, { useState } from 'react'
import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom'

const endpoint = 'http://localhost:8000/api/carta'
const endpointVecino = 'http://localhost:8000/api/vecino'

const CreateCarta = () => {
    const [remitente, setRemitente] = useState('');
    const [contenido, setContenido] = useState('');
    const [idPiso, SetIdPiso] = useState('');
    const navigate = useNavigate();
    const { id } = useParams();

    const store = async (e) => {
        e.preventDefault();
        await axios.post(endpoint, { remitente: remitente, contenido: contenido, id_piso: idPiso })
        navigate('/admin')
    }
    return (
        <div>
            <form onSubmit={store}>
                <div className='mb-3'>
                    <label className='form-label'>remitente</label>
                    <input
                        value={remitente}
                        onChange={(e) => setRemitente(e.target.value)}
                        type='text'
                        className='form-control'
                    />
                </div>
                <div className='mb-3'>

                    <label className='form-label'>Contenido</label>
                    <textarea
                        value={contenido}
                        onChange={(e) => setContenido(e.target.value)}
                        cols={20}
                        className='form-control'
                    />
                </div>
                <div className='mb-3'>
                    <label className='form-label'>piso</label>
                    <input
                        value={idPiso}
                        onChange={(e) => SetIdPiso(e.target.value)}
                        type='text'
                        className='form-control'
                    />
                </div>
                <button type='submit' className='btn btn-primary'>Enviar</button>
            </form>
        </div>
    )
}
export default CreateCarta
