		<div>
			<h1>Vecino : {nombre}</h1>
      <p>Apellido : {apellido}</p>
      <p>piso : {piso}</p>
			<table className='table'>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Remitente</th>
                        <th>Contenido</th>
                        <th>Id_piso</th>
                    </tr>
                </thead>
                <tbody>

                    {cartas.map((carta) => (
                        <tr key={carta.id}>
