import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import './ShowVecinos.css'

const endpoint = 'http://localhost:8000/api'

const ShowVecinos = () => {
    const [vecinos, setVecinos] = useState([])

    useEffect(() => {
        getAllVecinos()
    }, [])

    const getAllVecinos = async () => {
        const response = await axios.get(`${endpoint}/vecinos`)
        setVecinos(response.data);

    }
    const deleteVecino = async (id) => {
        await axios.delete(`${endpoint}/vecino/${id}`)
        getAllVecinos()
    }

    return (
        <div>

            <h1>VECINOS DE LA COMUNIDAD</h1>

            <ul class="listado-grid">

                {vecinos.map((vecino) => (
                    <Link to={'/'}>
                        <li class="card">
                        <img src="img_avatar.png" alt="Avatar" ></img>
                            <div class="contenido">
                                <h3>{vecino.nombre}</h3>
                                <div class="cardGrid">
                                    <p> Apellido : {vecino.apellido}</p>
                                    <p> Piso : {vecino.piso}</p>
                                </div>
                            </div>
                        </li>
                    </Link>

                ))}


            </ul>
        </div>
    )
}

export default ShowVecinos
