import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const endpoint = 'http://localhost:8000/api/cartaCartas/'
const cartaCartas = () => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [piso, setPiso] = useState('');
  const [cartas, setCartas] = useState([])
  const { id } = useParams();

  useEffect(() => {
    const getcartaCartasById = async () => {
      const response = await axios.get(`${endpoint}${id}`)
      setCartas(response.data)

    }
    getcartaCartasById()
  }, [])




  return (
    <div className='alinear'>

      <h1>carta </h1>
      <table className='table'>
        <thead>
          <tr>
            <th>Id</th>
            <th>Remitente</th>
            <th>Contenido</th>
            <th>id_piso</th>
          </tr>
        </thead>
        <tbody>

          {cartas.map((carta) => (
            <tr key={carta.id}>
              <td>{carta.id}</td>
              <td>{carta.remitente}</td>
              <td>{carta.contenido}</td>
              <td>{carta.id_piso}</td>
              <td>
                {/* <Link to={`/edit/${carta.id}`} className='btn btn-primary mr-2'> EDIT </Link> */}
                {/* <button onClick={() => deletecarta(carta.id)} className='btn btn-danger'>eliminar</button> */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>


    </div>
  )
}

export default cartaCartas
